const rot_z = math.matrix( [[0, -1, 0],
                            [1,  0, 0],
                            [0,  0, 1]])

p0 = [0.68, -0.63, 0.38]
p10 = [0.3, -0.31, 0.08]
p11 = [-0.142, 0.107, 0.02]
p1 = math.chain(rot_z).multiply(p11).add(p10).done();
p2 = [-0.17, -0.08, 0.09]
p3 = [-1.4, 0, 1.06] // origin p3 is [-1.4, 0.02, 1.06]
p4 = [0.05, 0, -1.14]

tx = flow.get('x')
ty = flow.get('y')
tz = flow.get('z')
j4 = [tx, ty, tz]

function length_of_vector(v) {
    if (math.count(v) == 2)
        return math.distance(v, [0, 0])
    else 
        return math.distance(v, [0, 0, 0])
}
function angle_of_vector(v1, v2){
    return math.evaluate(`acos(${math.multiply(v1,v2)}/(${length_of_vector(v1)}*${length_of_vector(v2)}))`)
}
// keep q1 = 0, for simplify
q1 = 0


// base_link <- Kabine
j0 = p0

// caluclate q0 in xy plane
// regardless q0, aj2 and distance of j2 to j0 is fixed 
// aj2 is the angle between -x axis at j2 with j2_j0
j0_xy = math.subset(p0, math.index([0,1]))

// q1 - Kabine <- Bagger_Verbindung_Arm
//             <- Arm1_zu_BaggerVerbindungArm2
// in local word of j0
j0_j1_xy = math.subset(p1, math.index([0,1]))
mq1 = math.matrix( [[math.cos(q1), -math.sin(q1), 0],
                    [math.sin(q1),  math.cos(q1), 0],
                    [           0,             0, 1]])
m1_j0 = math.chain(rot_z).multiply(mq1).done()
j0_j2 = math.chain(m1_j0).multiply(p2).add(p1).done()
j0_j2_xy = math.subset(j0_j2, math.index([0,1]))

axis_j4_j2_xy = math.chain(m1_j0).multiply([1,0,0])
        .subset(math.index([0,1])).done()
aj2 = angle_of_vector(j0_j2_xy, axis_j4_j2_xy)
a = length_of_vector(j0_j2_xy)

// back to global word (from base_link)
j4_xy = math.subset(j4, math.index([0,1]))
c = math.distance(j4_xy, j0_xy)
aj4 = math.evaluate(`asin(sin(${aj2})*${a}/${c})`)
aj0 = math.evaluate(`pi - ${aj2} -${aj4}`)

j0_j4_xy = math.subtract(j4_xy,j0_xy)
j0_base_xy = math.unaryMinus(j0_xy) 
angle_j4_j0_base = angle_of_vector(j0_j4_xy, j0_base_xy)
// when q0 = 0
j0_j2_xy = math.chain(rot_z).multiply(j0_j2)
        .subset(math.index([0,1])).done()
angle_j2_j0_base = angle_of_vector(j0_j2_xy, j0_base_xy)
q0 = angle_j2_j0_base + aj0 - angle_j4_j0_base 

// q0 is done
// update j1,j2

// q1 - Kabine <- Bagger_Verbindung_Arm
//             <- Arm1_zu_BaggerVerbindungArm2
mq0 = math.matrix( [[math.cos(q0),  -math.sin(q0), 0],
                    [math.sin(q0),   math.cos(q0), 0],
                    [           0,              0, 1]])
m0 = math.chain(rot_z).multiply(mq0).done()
j1 = math.chain(m0).multiply(p1).add(j0).done()

mq1 = math.matrix( [[math.cos(q1), -math.sin(q1), 0],
                    [math.sin(q1),  math.cos(q1), 0],
                    [           0,             0, 1]])
m1 = math.chain(m0).multiply(rot_z).multiply(mq1).done()
j2 = math.chain(m1).multiply(p2).add(j1).done()

// calculate q2, q3 in j2,j4, axis z plane
// mimic q2 - Arm1_zu_BaggerVerbindungArm2 <- Arm1
multiplier = -4.5774
offset = -0.4517

// when mq2 = 0
j2_p3_0 = math.chain(m1).multiply(p3).done()
j2_j4 = math.subtract(j4, j2)
a2_0 = angle_of_vector(j2_j4,j2_p3_0)

// for q2
// Law of Cosines
// c^2 = a^2 + b^2 - 2 * a * b * cos(a3)
a = length_of_vector(p3)
b = length_of_vector(p4)
c = math.distance(j4, j2)

a3 = math.evaluate(`acos((${a}^2+${b}^2-${c}^2)/(2*${a}*${b}))`)
// Law of Sines
// c/sin(a3) = a/sin(a4) = b/sin(a2)
a2 = math.evaluate(`asin(sin(${a3})*${b}/${c})`)

mq2 = a2 - a2_0 
// For forward kinematic, q2 = q2 * multiplier + offset
// For inverse kinematic
q2 = (mq2 - offset) / multiplier

mmq2 = math.matrix( [[ math.cos(mq2), 0, math.sin(mq2)],
                    [            0, 1,            0],
                    [-math.sin(mq2), 0, math.cos(mq2)]])
m2 = math.chain(m1).multiply(mmq2).done();

j3 = math.chain(m2).multiply(p3).add(j2).done()

// mimic q3 - Arm1 <- Arm2
multiplier = -5.1897
offset = -0.217

// calculate q3
// when mq3 = 0
a3_0 = math.pi - angle_of_vector(p4, p3)

// For forward kinematic, mq3 = q3 * multiplier + offset
// For inverse kinematic
mq3 = a3 - a3_0
q3 = (mq3 - offset) / multiplier

mmq3 = math.matrix( [[ math.cos(mq3), 0, math.sin(mq3)],
                    [            0, 1,            0],
                    [-math.sin(mq3), 0, math.cos(mq3)]])
m3 = math.chain(m2).multiply(mmq3).done();

j4 = math.chain(m3).multiply(p4).add(j3).done()
// Arm2 <- Loeffel
// target is j4 where Loeffel attached 

dp = j4

//flow.set('dx', j0[0]);
//flow.set('dy', j0[1]);
//flow.set('dz', j0[2]);
flow.set('dx', dp.subset(math.index(0)))
flow.set('dy', dp.subset(math.index(1)))
flow.set('dz', dp.subset(math.index(2)))

function jointMsg(value) {
    return {
        payload: value,
    }
}
outputMsgs = []
outputMsgs.push(jointMsg(q0))
outputMsgs.push(jointMsg(q1))
outputMsgs.push(jointMsg(q2))
outputMsgs.push(jointMsg(q3))

return outputMsgs;

